--

CREATE TABLE `mufaj` (
  `mufaj_id` int(11) NOT NULL,
  `mufaj_nev` text NOT NULL,
  `mufaj_leiras` text NOT NULL,
  `mufaj_kep` text NOT NULL DEFAULT '\'.png\''
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- A tábla adatainak kiíratása `mufaj`
--

INSERT INTO `mufaj` (`mufaj_id`, `mufaj_nev`, `mufaj_leiras`, `mufaj_kep`) VALUES
(1, 'Chick lit', 'A chicklit vagy chick lit egy modern irodalmi műfaj, a női lét kérdéseivel foglalkozik szórakoztató, gyakran humoros formában.\n', 'chicklit.png'),
(2, 'Dark Fantasy', 'Fantasy horror elemekkel vegyítve, illetve lazább kategorizálás szerint a sötétebb, negatív hangvételű fantasy műfajok is ide tartoznak. A végkifejlet ritkán pozitív.\r\n', 'darkfantasy.png'),
(3, 'Disztópia', 'A disztópia egy társadalom víziója, mely ellentétes az utópiával. Gyakran negatív jövőképet mutat. A disztopikus társadalmakban gyakran az életkörülmények nyomorúságosak, a szenvedés, a szegénység, az elnyomás, az erőszak, járványok és szennyeződések jellemzik.\r\n', 'disztopia.png'),
(4, 'Fantasy', 'A Fantasy fő jellemzője, hogy a tartalma többnyire olyan elképzelt, mitikus jellegű, valamilyen tekintetben mindig irreális, az író által teremtett, csak annak fantáziájában létező alternatív világokba, mint keretbe van ágyazva, mely felépítésében, fizikai és etikai törvényeiben, értékeiben, élőlényeiben, rokonságot mutat a vallások és mítoszok által megteremtett képzeletbeli világokkal.\r\n', 'fantasy.png'),
(5, 'Filozófia', 'A filozófia a világegyetem, a természet, az élet okával és céljával, a történelemben érvényesülő rendezőelvvel, a tudás és megismerés lehetőségével, a szépség, művészet és nyelv mibenlétével, a jogi-politikai normák természetével, a cselekedetek helyes vagy helytelen mivoltával, Isten és a transzcendencia létével foglalkozó tudományág.\r\n', 'filozofia.png'),
(6, 'Horror', 'A horror olyan irodalmi műfaj, amelynek középpontjában a félelem áll. Habár alapvetően irodalmi művekről van szó, megjelenhet más ágazatokban is, melyek közül a legtipikusabb a film.\r\n', 'horror.png'),
(7, 'Ifjúsági ', 'Az gyermek- és ifjúsági irodalom a gyermekek, a komolyabb művek befogadására még éretlen fiatalok számára írt, az ő igényeiket kielégítő szépirodalmi művek összessége. A gyermekirodalom alkalmazott irodalom is. Gyermek, ill. ifjú főhőse van a legtöbb gyermekirodalmi műveknek.\r\n', 'ifjusagi.png'),
(8, 'Krimi', 'A krimi vagy bűnügyi regény az epika (elbeszélő irodalom) egy népszerű tematikus műfaja, amely a bűntények felderítésével, elkövetésük bemutatásával, illetve lélektani indítóokaik vizsgálatával, valamint a bűnözők, nyomozók („detektívek”) és bűnügyi rendőrök világával foglalkozik.\r\n', 'krimi.png'),
(9, 'Lányregény', 'Fiatal lányok olvasókörének szánt érzelmes, kedves, romantikus, ugyanakkor gyermeteg alkotások.\r\n', 'lanyregeny.png'),
(10, 'Napló', 'Napló – naponként (vagy meghatározatlan időközökben) készített magánjellegű feljegyzések sorozata.\r\n', 'naplo.png'),
(11, 'New Adult', 'Az ifjúsági irodalom felső határa, amikor a főszereplő, már nem tinédzser, de még nem középkorú (19-25 éves), nagy változás zajlik le a életében, többször komoly tragédia áll a történet hátterében.\r\n', 'newadult.png'),
(12, 'Novella', 'A novella a szépirodalomhoz tartozó kisepikai műfaj. Általában egy szálon fut, és egyetlen sorsfordulat köré épül. Legtöbbször kevés szereplő jelenik meg benne. Lezárására jellemző a csattanó.\r\n', 'novella.png'),
(13, 'Paródia', 'Mai értelmében egy többnyire közismert mű, műfaj, zenemű, előadó komikus hatású utánzása, ironizálása szórakoztató céllal. És lehet parodizálni jelenségeket, eseményeket is. Továbbá a szerző akár önmagát is parodizálhatja.\r\n', 'parodia.png'),
(14, 'Posztapokaliptikus', 'Egy látomásszerű világkép, melyben egy világméretű katasztrófa zajlott le és emiatt a világ megváltozott. Ez a katasztrófa utáni állapot legtöbbször negatív, baljós és sivár.\r\n', 'posztapokaliptikus.png'),
(15, 'Pszichothriller', 'A thriller jegyeit hordozza magán, de a középpontban mindig egy olyan pszichopata áll, akitől a hideg futkos a hátunkon.\r\n', 'pszichothriller.png'),
(16, 'Romantikus', 'Olyan alkotások, amelyekben a felfokozott életérzés kifejezése, a reményvesztettség, a csalódás, a világfájdalom vagy éppen az ezzel ellentétes forradalmi hevület és lángoló életöröm van jelen.\r\n', 'romantikus.png'),
(17, 'Sci-fi', 'A sci-fi az angol science fiction kifejezés. Jelentése: tudományos-fantasztikus. A tudományos-fantasztikus mű olyan irodalmi alkotás, mely legtöbbször valódi vagy képzeletbeli tudományoknak a társadalomra, vagy egyes egyénekre gyakorolt hatását mutatja be. Ezeknek a műveknek a közös jellemzői, hogy zömében egy lehetséges jövőben játszódó képzeletbeli történetek.\r\n', 'scifi.png'),
(18, 'Steampunk', 'Elem a steampunkban az elképzelt technológiai vívmányok és találmányok szerepeltetése\r\n', 'steampunk.png'),
(19, 'Szatíra', 'Jellemzője, hogy a valóság komikus-ironikus ábrázolására törekszik. Ábrázolási módszerei között szerepel az arányok megváltoztatása, a kicsinyítés, a nagyítás, a túlzás.', 'szatira.png'),
(20, 'Szépirodalmi', 'Az irodalomban olyan történetek, színdarabok és költemények összessége, amelyek művészeti alkotásként határozhatók meg.\r\n', 'szepirodalmi.png'),
(21, 'Thriller', 'A thriller az angol „thrill” szóból jön, ami borzongást jelent. Ennek megfelelően az ismeretlentől való félelmet idézi fel. Többnyire igaz a műfaj szereplőire, hogy megéreznek egy bizonyos bajt, mely fenyegeti őket, de sokáig nem szembesülnek azzal – ahogy a néző sem –, ezzel jön létre a kívánt feszültség, izgalom a nézőben.\r\n', 'thriller.png'),
(22, 'Tragédia', 'A tragédia valami szomorú történetet dolgoz fel, ami többnyire rosszul végződik és tanulságot foglal magába.\r\n', 'tragedia.png'),
(23, 'Történelmi', 'Régi korokban játszódó történetek, amik nem feltétlenül a régi korokban íródtak.\r\n', 'tortenelmi.png'),
(24, 'Urban Fantasy', 'Hasonlít a történelmi fantasyhoz, de mai korban játszódik, ahol a mágia és/vagy a mitikus lények többé-kevésbé titokban jelen vannak.\r\n', 'urbanfantasy.png'),
(25, 'Regény', 'A regény rendszerint hosszabb időszakot felölelő, szerteágazó cselekményű, számos szereplőt (fő- és mellékszereplőket) bemutató mű, az epika vezető műfaja. A mű középpontjában egy történet áll. Formája elsősorban próza, korábbról ismerünk azonban verses regényeket is. Jellemzője a nagy (több száz, esetenként több ezer oldalas) terjedelem.[1] A regények – fajtájuk, témájuk, és nem utolsósorban szerzőjük képességének függvényében – változatos arányban állítják fókuszba magát a cselekményt, a fizikai és társadalmi környezetet, az egyes szereplők érzés- és gondolatvilágát, illetve a jellemek fejlődését. Röviden: Regény: Epikai műfaj, hosszú terjedelmű, sok szereplőjű. A cselekménye több szálon fut, melyeket epizódok szakíthatnak meg. Ezek a szálak a tetőpontban találkoznak.', 'regeny.png'),
(26, 'Szakácskönyv', 'A szakácskönyv konyhai alapanyagok (húsok, zöldségek, gyümölcsök, gabonafélék, tészták, fűszerek stb.), főzési módszerek és technológiák, valamint gasztronómiai receptek leírását tartalmazó, gyakran gazdagon illusztrált gyűjtemény.', 'szakacskonyv.png'),
(27, 'Életmód', 'Jellemző tevékenységek; az általában végzett elfoglaltságok, szokásos cselekvések', 'eletmod.png'),
(28, 'Dráma', 'Irodalmi műnem, alapvetően párbeszédekre (dialógusokra) épülő, többnyire színházi előadásra szánt irodalmi alkotások (drámák, drámai művek, illetve színművek) összefoglaló elnevezése.', 'drama.png'),
(29, 'Oktató, ismeretterjesztő', 'Valamilyen témában próbál ismeretet átadni.', 'oktato.png'),
(30, 'Humoreszk', 'A humoreszk prózai kisepikai műfaj. Fő jellemvonása a karcolatszerű, rövid terjedelem és az ennek megfelelő, fő vonásokra redukált kidolgozás, valamint a könnyed, kedélyesen derűs hangulatú humor. A 19. századi, klasszikusnak nevezett válfajára az anekdotikus elemek a jellemzőek.', 'humoreszk.png'),
(31, 'Kalandregény', 'A kalandregény egy olyan regénytípus, amelyben a történet középpontjában a főszereplő kalandos helyzetei, megpróbáltatásai vagy hőstettei állnak.', 'kaland.png');

